import React from 'react';
import { useApp } from '../../context/AppContext';

const Header = ({ onCreateTask, onShowFiles, onShowSettings, onForceSave }) => {
    const { userProgress } = useApp();

    const headerStyle = {
        background: 'white',
        borderRadius: '12px',
        padding: '20px',
        marginBottom: '20px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        flexWrap: 'wrap',
        gap: '15px'
    };

    const logoStyle = {
        fontSize: '24px',
        fontWeight: 'bold',
        color: '#4f46e5'
    };

    const userStatsStyle = {
        display: 'flex',
        alignItems: 'center',
        gap: '20px',
        flexWrap: 'wrap'
    };

    const levelBadgeStyle = {
        background: 'linear-gradient(45deg, #4f46e5, #10b981)',
        color: 'white',
        padding: '8px 16px',
        borderRadius: '20px',
        fontWeight: 'bold'
    };

    const pointsStyle = {
        color: '#10b981',
        fontWeight: '600'
    };

    const buttonStyle = {
        padding: '10px 16px',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        fontWeight: '500',
        transition: 'all 0.2s ease'
    };

    const primaryButtonStyle = {
        ...buttonStyle,
        background: '#4f46e5',
        color: 'white'
    };

    const secondaryButtonStyle = {
        ...buttonStyle,
        background: '#f3f4f6',
        color: '#374151'
    };

    return (
        <header style={headerStyle}>
            <div style={logoStyle}>üéØ Priority Task Manager</div>
            
            <div style={userStatsStyle}>
                <div style={levelBadgeStyle}>Level {userProgress.level}</div>
                <div style={pointsStyle}>{userProgress.points} points</div>
                <div style={{ 
                    fontSize: '12px', 
                    color: '#6b7280',
                    background: '#f9fafb',
                    padding: '4px 8px',
                    borderRadius: '12px',
                    border: '1px solid #e5e7eb'
                }}>
                    Auto-save Ready
                </div>
            </div>
            
            <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
                <button style={primaryButtonStyle} onClick={onCreateTask}>
                    + New Task
                </button>
                <button style={secondaryButtonStyle} onClick={onShowFiles}>
                    üìÅ Files
                </button>
                <button style={secondaryButtonStyle} onClick={onShowSettings}>
                    ‚öôÔ∏è Settings
                </button>
                <button style={secondaryButtonStyle} onClick={onForceSave}>
                    üíæ Save Now
                </button>
            </div>
        </header>
    );
};

export default Header;
