import React from 'react';
import { AppProvider, useApp } from './context/AppContext';
import './styles/globals.css';

// Main component that uses the context
const PriorityTaskManager = () => {
  const { userProgress, priorityCategories } = useApp();

  return (
    <div className="app">
      <h1>üéØ Priority Task Manager</h1>
      <p>‚úÖ React setup complete!</p>
      <p>‚úÖ Context connected!</p>

      <div style={{ marginTop: '20px', padding: '15px', background: 'white', borderRadius: '8px' }}>
        <h3>User Progress:</h3>
        <p>Level: {userProgress.level}</p>
        <p>Points: {userProgress.points}</p>

        <h3>Priority Categories: {priorityCategories.length}</h3>
        {priorityCategories.map(cat => (
          <div key={cat.id} style={{ margin: '5px 0', fontSize: '14px' }}>
            <span style={{ color: cat.color }}>‚óè</span> {cat.english} ({cat.hebrew})
          </div>
        ))}
      </div>
    </div>
  );
};

// Main App with Provider
function App() {
  return (
    <AppProvider>
      <PriorityTaskManager />
    </AppProvider>
  );
}

export default App;
